bool channelsMormThan1(const QImage& img) {
    // based on Qt 5.14.2
    // QImage::Format follow https://doc.qt.io/qt-5/qimage.html
    int format = img.format();
    if ((format >= 4 && format <= 22) || (format >= 25 && format <= 29 && format != QImage::Format_Grayscale16))
        return true;
    else
        return false;
}

QImage convertGray16ToGray8(const QImage& img) {
    int width = img.width();
    int height = img.height();

//    uchar* data8 = new uchar[width*height]();
//    QImage ret;
    QImage ret = QImage(width, height, QImage::Format_Grayscale8);

    double mmax = 0, mmin = 0;
    quint16 val = 0;

    for (int y = 0 ; y < height; y++) {
        quint16 *dst = const_cast<quint16*>(reinterpret_cast<const quint16*>(img.scanLine(y)));
        for (int x = 0; x < width; x++) {
            val = dst[x];
            mmax = val > mmax?val:mmax;
            mmin = val < mmin?val:mmin;
        }
    }
    for (int y = 0 ; y < height; y++) {
        quint16 *dst = const_cast<quint16*>(reinterpret_cast<const quint16*>(img.scanLine(y)));
        uchar* data8 = ret.scanLine(y);
        for (int x = 0; x < width; x++) {
            data8[x] = (dst[x] - mmin) / (mmax - mmin) * 255;
        }
    }
    return ret;
}
